import { Post } from './types'

export class Posts {
  private ws: Promise<WebSocket>
  constructor() {
    this.ws =  asyncWebSocket('ws://127.0.0.1:3001/posts')
  }

  getPosts() {
    return fetch('http://127.0.0.1:3001/get_posts')
  }
  sendPost(post: Post) {
    return fetch(`http://127.0.0.1:3001/send_post?name=${post.name}&text=${post.text}`)
  }
}

// давай для начала объясню, что я тут написал
// по сути смысл данной функции в том, чтобы создать вебсокет 1
// 2 - добавить колбек, который выполнится, когда сокет откроется (будет готов)
// 3 - вернуть результат в виде промиса, чтобы потом можно было дождаться готовности вебсокета
// прост если не ждать, то ты видела, какая ошибка получается
function asyncWebSocket(url: string): Promise<WebSocket> {
  return new Promise((resolve, reject) => {
    const ws = new WebSocket(url) // (1)
    ws.onopen = () => resolve(ws) // (2 - 3)
  })
}
