import { Post } from './types'

export class Posts {
  private ws: Promise<WebSocket>
  constructor() {
    this.ws =  asyncWebSocket('ws://127.0.0.1:3002/posts')
  }

  async getPosts() {
    const ws = await this.ws;
    // просто ща вот мы отправили запрос, что хотим посты
    ws.send(JSON.stringify({ action: 'get_posts' }))
    // нам ответ придется ловить в другом месте, или делать(и что это значит?) так:
    // это значит мы не сможем вернуть ответ тут же, надо немного переписат код, чтобы он стал .ммм
    // ориентированным на события, зависимым от сервера
    // а сейчас (через фетч) как бы наоборот, МЫ по своей инициативе спрашиваем сервер и ответ тут же получаем
    // ниче не понятно пока
    return new Promise(resolve => {
      ws.onmessage = (ev) => resolve(ev.data)
    })
    // return fetch('http://127.0.0.1:3001/get_posts')
  }
  sendPost(post: Post) {
    return fetch(`http://127.0.0.1:3001/send_post?name=${post.name}&text=${post.text}`)
  }
}

function asyncWebSocket(url: string): Promise<WebSocket> {
  return new Promise((resolve, reject) => {
    const ws = new WebSocket(url)
    ws.onopen = () => resolve(ws)
  })
}
