import { FullPost, Post } from './types'

const serverAddress = '127.0.0.1'
// 'api-cozy-space.herokuapp.com'

export class Posts {
  private ws: WebSocket
  private handleNewPost?: (post: FullPost) => void 
  
  constructor() {
    this.ws = new WebSocket(`ws://${serverAddress}`)
    this.ws.onmessage = event => { 
      this.handleNewPost?.(JSON.parse(event.data))
    }
  }

  onNewPost(callback: (post: FullPost) => void) {
    this.handleNewPost = callback
  }

  getPosts() {
    return fetch(`http://${serverAddress}:3001/get_posts`)
  }
  sendPost(post: Post) {
    return fetch(`http://${serverAddress}:3001/send_post?name=${post.name}&text=${post.text}`)
  }
}
