import React from 'react'
import styled from 'styled-components'
import asteroid from './assets/asteroid.svg'


const Wrapper = styled.section`
width: 1200px;
max-width: 100%;
margin: 0 auto;
padding: 0 20px 0;
`

const Title = styled.h1`
  font-size: 56px;
  text-align: center;
  font-family: "Tourney", cursive;
  font-weight: 600;
  background: linear-gradient(45deg, #41265c 33%, #2efef7 66%, #5f04b4);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`

const Img = styled.img`
width: 60px;
height: 60px;
max-width: 100%;
`

const Form = styled.div`
width: 800px;
max-width: 100%;
display: flex;
flex-direction: column;
`

const Input = styled.input`
width: 100px;
max-width: 100%;
padding: 10px;
background-color: #372c42;
border: 1px solid #1e1d1f;
border-radius: 10px;
outline: none;
`

const Textarea = styled.textarea`
width: 400px;
max-width: 100%;
padding: 10px;
border-radius: 15px;
outline: none;
border: 1px solid #1e1d1f;
background-color: #372c42;
`

const Button = styled.button`
width: 160px;
height: 40px;
outline: none;
border-radius: 30px;
background-color: #5f04b4;
border: none;
box-shadow: 5px 4px 5px 5px #2efef7 #5f04b4;
color: #2efef7;
font-size: 24px;
font-family: "Tourney", cursive;
font-weight: 300;
text-align: center;
`

const Label = styled.label`
color: #372c42;
`

function App() {
  return (
    <Wrapper>
      <Title>Cozy Space <Img src={asteroid} alt='Asteroid'/></Title>
      <Form method='POST'>
        <Label>Your name</Label>
        <Input defaulValue='Guest'/>
        <Label>Share what`s in your mind</Label>
        <Textarea placeholder='Type your post here...' rows="5" cols="33"/> 
        <Button>Add post</Button>
      </Form>
    </Wrapper>
  );
}

export default App
