import React from 'react'
import styled from 'styled-components'
import asteroid from './assets/asteroid.svg'
import { PostsList } from './PostsList'
import { Posts } from './Data'
import { PostSendForm } from './PostSendForm'
import { Post } from './types'
import { FullPost } from './types'


const Wrapper = styled.section`
  max-width: 100%;
  margin: 0 auto;
  padding: 0 20px 15px;
`
const Main = styled.section`
  height: 100vh;
`
const Title = styled.h1`
  font-size: 56px;
  line-height: 80px;
  text-align: center;
  font-family: 'Tourney', cursive;
  font-weight: 600;
  background: linear-gradient(45deg, #41265c 33%, #2efef7 66%, #5f04b4);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  @media (max-width: 320px) {
    margin-top: 5px;
    margin-bottom: 0;
  }
`

const Img = styled.img`
  width: 60px;
  height: 60px;
  max-width: 100%;
`

const postsInteractive = new Posts()

export function App() {
  const [posts, setPosts] = React.useState<FullPost[]>([])

  const onSend = async (post: Post) => {
    await postsInteractive.sendPost(post)
    // и все) теперь 100% будет обновляться. Эвейт очень интуитивный, правда? ага работает
    fetchPosts() //прост  так? тут можно не ждать, так как далее мы ничего не делаем все-равно
    // почти, можешь ща чекнуть, что будет // ща терминал лагает, чет да //каой там адрес? 127...? ты тут эй?
    // можно http://localhost:3000, хмм или не можно) 
    // 127.0.0.1:300 так надо 0, а не 1 лел  
    // у меня такой адрес 3001(я спецом сделала) а клиент на 3000// а уже грузит сек мама зашла
    // 3001 это сервер же? да, а нам клиент надо же, вот он только захостился
    // всетаки локалхост, в моем случае точно, а то КОРС ругается)

    // и потом подумаем, почему так. Мы упустили одну маленькую деталь, но важную
    // вот тут надо обновить список постов:
    // получить их с сервера    -\___эти два действия мы уже делаем.., где?
    // поместить в стейт posts  -/
    // а значит тут достаточно что сделать? эм подскажи? 
    // раз мы уже умеем это делать, то можно переиспользовать функцию fetchPosts(), 
    // просто вызвав ее после отправки постов
    // та в смысле просто вызвать // я тут
    // чекай ща сообщение, напиши что-то т отправь, а потом еще отправляется еее и на сервере есть 
    //сразу пояаилсь -- скорее всего нам повезло) ахах это как? почему но так не должно быть
    // Я вот ща ченул, со второго раза только отправилось: 
    //у меня с первого тогда отправилось, если интернет будет медленнее, то не сработает
    // Так происходит потому, что после отправки поста нужно ДОЖДАТЬСЯ, пока сервер отправит этот пост в бд и ответит нам 200
    // сразу обновилось или только на второй раз?
    // асинхронность нужна?
    // напишу на русском, а ты закодишь:
    // нужно подождать когда отправятся посты
    // а потом уже фетчПостс// ок ща подумаю
    //так шо дальше? 
    // шо там я писал? шото про сортировкуsort(колбек) шо это значит
    // значит должно быть так массив.sort((что-то-тут) => что-то-тут)
    // будем сортировать так, чтобы были новые посты наверху(так конечно же)
    // как предпочтительнее сортировать? на сервере?  ну вот го на сервер)
    // где будем сортировать, на сервере или на клиенте?
    // в идеале да, потому что если там будет 100+ постов, то в браузере это не круто)
  }

  const fetchPosts = async () => { // как тут примерно, да
    // тут мы сначла дожидаемся получения постов, потом берем их из промиса и кладем в posts
    const newPosts = await postsInteractive.getPosts()
    setPosts(await newPosts.json())
  }

  React.useEffect(() => {
    fetchPosts()
    console.log('Fetch is working')
  }, [])

  return (
    <Wrapper>
      <Main>
        <Title>
          Cozy Space <Img src={asteroid} alt="Asteroid" />
        </Title>
        <PostSendForm onSend={onSend} />
      </Main>
      <PostsList posts={posts} />
    </Wrapper>
  )
}
