import { FullPost, Post } from './types'

export class Posts {
  private ws: WebSocket
  private handleNewPost: (post: FullPost) => void
  // -----------------^---добавь сюда вопросительный знак 
  
  constructor() {
    this.ws =  new WebSocket('ws://127.0.0.1:3001/posts')
    // и он ща ругается, что мы создали поле и ничего туда не присвоили (а объявили, что оно функция)
    // мы можем пойти двумя путями:
    // 1. сказать, что эта функция (это поле) опциональное и может быть пустым (undefined) ? mb
    // 2. можем в конструкторе присвоить туда пустую функицю
    // оба способа ок, какой тебе нрав?

  }
  // давай для начала добавим метод onNewPost((post: Post) => void) такой
  // эта функция будет устанавливать колбек, коотоый будет вызываться при получении нового поста
  // вот (post: Post) => void -- это то, каким должен быть колбек, он будет принимать 1 пост и ничего не возвращать
  // мы кстати уже можем заюзать этот метод в App, го туда
  // Аня? что? вот такое крч тут коллбек?это никакие правила не нарушает? ага, мы его будем вызывать
  // нет, наоборот, реактивное программирование) от слова "реакция" на что-то (изменения/события)
  //  эм что это это ты придумал? нет, "реактивное прграммирование" потом загуглить надо (тот же реакт поэтому так зовется)
  onNewPost(callback: (post: FullPost) => void) {
    this.handleNewPost = callback // вот так ))и шо это за бред?
    // мб это мой тайпскрипт хиро тогда
    // только надо объявить handleNewPost как приватное поле, это можно чере Ctrl+точка сделать такого нет
    // не бред, мы этот handleNewPost теперь можем вызывать - а вызовется наш колбек из App (в котором сетПостс)
    
    
  }
  getPosts() {
    return fetch('http://127.0.0.1:3001/get_posts')
  }
  sendPost(post: Post) {
    return fetch(`http://127.0.0.1:3001/send_post?name=${post.name}&text=${post.text}`)
  }
}
