import React from 'react'
import styled from 'styled-components'

const PostMessage = styled.div` // тут не правильно думаю. это так что б заполнить
  display: flex;
  flex-wrap: wrap;
`

const UserName = styled.div`
  color: #fff;
  font-size: 12px;
  font-weight: 900;
  font-family: 'Tourney', cursive;
`
const PostUploadTime = styled.time`
  font-size: 8px;
  color: #6c6c6c;
`

const TextMessage = styled.div`
  max-width: 400px;
  min-height: 100px;
  padding: 15px;
  border-radius: 30px;
  background-color: #746087;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`
