// поля хоть как можно назвать, я предлагаю кратко name, date, text. Только lowerCameCase'ом, т.к. это просто поля js объекта будут
// ну вот, у нас ща есть эти поля, давай прям так их и напишу ща в JDoc

/**
 * @param {{name: string; date: string; text: string}} props 
 */
export function PostItem(props) {
  return (
    <PostMessage>
      <UserName>{props.name}</UserName>
      <PostUploadTime>{props.date}</PostUploadTime>
      <TextMessage>{props.text}</TextMessage>
    </PostMessage>
  )
}


