import * as React from 'react'
import styled from 'styled-components'
import { Post } from './types'

const Form = styled.div`
  top: 0;
  position: sticky;
  margin: 0 auto;
  margin-bottom: 5%;
  padding-top: 5%;
  @media (max-width: 320px) {
    padding-top: 5px;
  }

  max-width: 400px;
  display: flex;
  flex-direction: column;
`

interface ValidProps {
  error: boolean;
}

const Input = styled.input<ValidProps>`
  padding: 10px;
  background-color: #746087;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  border-radius: 10px;
  outline: none;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Textarea = styled.textarea<ValidProps>`
  min-height: 100px;
  padding: 10px;
  border-radius: 15px;
  outline: none;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  background-color: #746087;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Button = styled.button`
  margin-top: 7%;
  max-width: 120px;
  max-height: 40px;
  outline: none;
  border-radius: 30px;
  background: linear-gradient(45deg, #66538f 30%, #7984e8 60%, #2d3159);
  border: transparent;
  color: #000;
  font-size: 28px;
  font-family: 'Bungee Shade', cursive;
  font-weight: 900;
  cursor: pointer;
  letter-spacing: 1px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  transition-duration: 0.5s;
  top: 0;
  position: relative;
  &:hover {
    top: 5px;
  }
`

const Label = styled.label`
  margin-top: 25px;
  margin-bottom: 8px;
  color: #746087;
  font-size: 20px;
  font-family: 'Tourney', cursive;
`

interface Props {
  onSend: (post: Post) => void
}

export function PostSendForm(props: Props) {
  const [name, setName] = React.useState('')
  const [text, setText] = React.useState('')

  const [validName, setValidName] = React.useState(true)
  const [validText, setValidText] = React.useState(true)

  const onButtonClick = () => { // туть да
    const post: Post = {
      name,
      text,
    }
    
    if(trim(name && text)){
      props.onSend(post) 
    } else {
      // вот тут ерор бы высвечивать там
      // хотя что б при блюре высвечивалась подпись введите имя текст, а при отправке прям ошибко шоб кидало
      // ты хочешь еррор высвечивать только при отпрвке? не после потери фокуса?
      // ну это чуть избыточно уже, но для учебных целей почему бы и нет почем уизбыточно?? я такое вроде часто видела
      // ну я думала сегодня сделаю уже...а то просто завтра хз как, потом переезд, потом хз какой инет и условия..
      // потом дополним тут
      // ну мб мне только так кажется, тут потом крч, мб сама даже сделаешь, как домашка)
      // ну ок
      // но сначала го проверим  у меня пустота отправилась без пробела аааа это ты блять))))) 
      // пробелы можно отправлять - плохо
      // да))
      // щас надо вспомнить, что я говорил про метод trim() у строк /там где-то точку надо пишет
      // тут строки это name и text же, все остальное не строки
      // где? ошибку пишет? мб вскод тупит?
      // 
      // v, 
    } // Ой ахахах не то сделалал
    setText('')
  }

  const onKeyEnter = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      onButtonClick()
      e.preventDefault()
    }
  }

  const onNameBlur = () => { 
    setValidName(!!name)
  }

  const onTextBlur = () => {
    setValidText(!!text)
  }

  return (
    <Form>
      <Label>Your name</Label>
      <Input
        value={name}
        onChange={(e) => setName(e.target.value)} 
        error={!validName} 
        onBlur={onNameBlur}
      />
      <Label>Share what`s in your mind</Label>
      <Textarea
        value={text}
        onKeyPress={onKeyEnter}
        onChange={(e) => setText(e.target.value)} 
        error={!validText}
        onBlur={onTextBlur}
      />
      <Button onClick={onButtonClick}>go!</Button>
    </Form>
  )
}

// сначала важное, потом красивое)00 полностью с тобой согласен :3
