import { Post } from './types'

export class Posts {
  private ws: WebSocket
  constructor() {
    this.ws = new WebSocket('ws://127.0.0.1:3001')
  }
  // думаю лучше это сделать в конструкторе здесь 
  // напомни плиз, я помню что там супер есть, супер есть ТОЛЬКО если мы наследуемся, а тут нет наслеования
  // так что можно голый контруктор писать, просто constructor() {...тут инициализация веб сокета} И как его инициализировать?
  // в смысле создать new WebSocket()
  // и записать в приватное поле класса мб, this.ws = new WebSocket(), шо там, аргументы еще адрес сервера видимо
  // а, надо чуть выше конструктора объявить поле класса
  // да, я ща 10 сек
  // private ws: WebSocket так, вроде ок м? проинициализировали, о идее в этот момент ща сделается запрос и сервер вернет посты
  // го тестить в браузере, запусти все
  // расшарила 
  // эмм, ща, вот теперь расшарилось)
  getPosts() {
    return fetch('http://127.0.0.1:3001/get_posts')
  }
  sendPost(post: Post) {
    return fetch(`http://127.0.0.1:3001/send_post?name=${post.name}&text=${post.text}`)
  }
}
