import React from 'react'
import styled from 'styled-components'
import asteroid from './assets/asteroid.svg'

//Приветствую в царствии реактиуса ))000
// дороу))
const Wrapper = styled.section`
max-width: 100%;
margin: 0 auto;
padding: 0 20px 15px; // по идее тут отступы со всех сторон задаются? Ну мб так нижний 15 ну мобайл чекни айфин 5 (думаю такие еще есть у людей)
/* чет я не вижу разницы, на каком размере мобайл чекать? лол кнопка вообще вниз уехала)0 та де блять
кину в тг

*/
`

const Title = styled.h1`
  font-size: 56px;
  line-height: 80px;
  text-align: center;
  font-family: "Tourney", cursive;
  font-weight: 600;
  background: linear-gradient(45deg, #41265c 33%, #2efef7 66%, #5f04b4);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`

const Img = styled.img`
width: 60px;
height: 60px;
max-width: 100%;
`

const Form = styled.div`
margin: 0 auto;
padding-top: 5%;
max-width: 400px;
display: flex;
flex-direction: column;
`

const Input = styled.input`
/* flex: 1 1 0; */
padding: 10px;
background-color: #746087;
border: 1px solid #1e1d1f;
border-radius: 10px;
outline: none;
font-family: "Tourney", cursive;
font-weight: 800;
`

const Textarea = styled.textarea`
/* flex: 1 1 0; */
min-height: 100px;
padding: 10px;
border-radius: 15px;
outline: none;
border: 1px solid #1e1d1f;
background-color: #746087;
font-family: "Tourney", cursive;
font-weight: 800;
`

const Button = styled.button`
margin-top: 3rem;
max-width: 120px;
max-height: 40px;
outline: none;
border-radius: 30px;
background: linear-gradient(45deg, #66538f 30%, #7984e8 60%, #2d3159);
border: none;
color: #000;
font-size: 26px;
font-family: "Tourney", cursive;
font-weight: 700;
text-align: center;
cursor: pointer;
letter-spacing: 1px;
text-transform: uppercase;
`

const Label = styled.label`
margin-top: 25px;
margin-bottom: 8px; // ну тут я хз как иначе это между ними расстояние, понимаю, ща покажу как// отличное оправдание)0 а че? а у нас тут экпресс и нода будет?
/* да */// ее:3 надо сделать что-то с тайтлом да? ну я думала мб будет красивее если лого будет на уровне тайтла? или как?
/* это как на уровне тайтла? типа рядом с текстом иконка с метеором? */ // ну для мобайл да
/* я предлагаю чуть другое, увеличить интервал между строками тайтла и будет ок, ща */
/* хотя лан, не покажу)) ето же маленький проект, тут можно и так */
/* но в больших лучше избегать суммирования марджинов - вот */
color: #746087;
font-size: 20px;
font-family: "Tourney", cursive;
`

function App() {
  return (
    <Wrapper>
      <Title>Cozy Space <Img src={asteroid} alt='Asteroid'/></Title>
      <Form method='POST'>
        <Label>Your name</Label>
        <Input/>
        <Label>Share what`s in your mind</Label>
        <Textarea/> 
        <Button>post</Button>
      </Form>
    </Wrapper>
  );
}

export default App
 //што? 
 //я хз) оставлять ли градиент аааа 
//  это ты ща написала или я тормоз?) та до кек
