  }
}

export function PostSendForm(props: Props) {
  const [name, setName] = React.useState('')
  const [text, setText] = React.useState('')
  const [invalid, setInvalid] = React.useState('name' || 'text' || ' ') 


  const onButtonClick = () => {
    const post: Post = {
      name,
      text,
    }

    props.onSend(post)
    setText('')

      if(!name && !text) { 
        setInvalid('both')
      } else if(!name) {
        setInvalid('name') 
      } else if(!text) {
        setInvalid('text') 
      } else {
