import * as React from 'react'
import styled from 'styled-components'
import { Post } from './types'

const Form = styled.div`
  top: 0;
  position: sticky;
  margin: 0 auto;
  margin-bottom: 5%;
  padding-top: 5%;
  @media (max-width: 320px) {
    padding-top: 5px;
  }

  max-width: 400px;
  display: flex;
  flex-direction: column;
`

interface ValidProps {
  error: boolean;
}

const Input = styled.input<ValidProps>`
  padding: 10px;
  background-color: #746087;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  border-radius: 10px;
  outline: none;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Textarea = styled.textarea<ValidProps>`
  min-height: 100px;
  padding: 10px;
  border-radius: 15px;
  outline: none;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  background-color: #746087;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Button = styled.button`
  margin-top: 7%;
  max-width: 120px;
  max-height: 40px;
  outline: none;
  border-radius: 30px;
  background: linear-gradient(45deg, #66538f 30%, #7984e8 60%, #2d3159);
  border: transparent;
  color: #000;
  font-size: 28px;
  font-family: 'Bungee Shade', cursive;
  font-weight: 900;
  cursor: pointer;
  letter-spacing: 1px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  transition-duration: 0.5s;
  top: 0;
  position: relative;
  &:hover {
    top: 5px;
  }
`

const Label = styled.label`
  margin-top: 25px;
  margin-bottom: 8px;
  color: #746087;
  font-size: 20px;
  font-family: 'Tourney', cursive;
`

const ErrorText = styled.div`
  font-family: 'Tourney', cursive;
  margin-top: 3rem;
  margin-bottom: 5%;
  color: red;
  text-align: center; 
  font-size: 22px;     
  font-weight: 600;
  border: 1px solid red;
  border-radius: 10px;
  box-shadow: 1px 1px 3px 3px red, 1px 1px 3px 3px red;
  min-width: 400px;
  max-height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
`

interface Props {
  onSend: (post: Post) => void
}

export function PostSendForm(props: Props) {
  const [name, setName] = React.useState('')
  const [text, setText] = React.useState('')

  const [validName, setValidName] = React.useState(true)
  const [validText, setValidText] = React.useState(true)
  const [hasError, setHasError] = React.useState(false);

  const onButtonClick = () => {
    const post: Post = {
      name,
      text,
    }
    
    if (name.trim() && text.trim()){
      props.onSend(post) 
      setHasError(false) // если отпавил норм имя+текст, то ошибку скрываем
    } else {
      setHasError(true) // ошибка что юзер дурак (само отображение в верстке внизу)
    }
    setText('')
  }

  const onKeyEnter = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      onButtonClick()
      e.preventDefault()
    }
  }

  const onNameBlur = () => { 
    setValidName(!!name)
  }

  const onTextBlur = () => {
    setValidText(!!text)
  }

  return (
    <Form>
      <Label>Your name</Label>
      {/* 1) для этих маленьких ошибок сделать еще одну компоненту для отображения ошибки */}
      {/* 1.1) я бы назвал ErrorText, а ту большую переименовал в ErrorBox */}
      {/* 2) и тут уже как изначально предлагал: */}
      {/* <ErrorText>{валидно ли name ? 'Please enter the name!' : ''}</ErrorText> */}
      {'Please enter the name!'}
      <Input
        value={name}
        onChange={(e) => setName(e.target.value)} 
        error={!validName} 
        onBlur={onNameBlur}
      />
      <Label>Share what`s in your mind</Label>
      {'You can`t send empty messages :<'}
      <Textarea
        value={text}
        onKeyPress={onKeyEnter}
        onChange={(e) => setText(e.target.value)} 
        error={!validText}
        onBlur={onTextBlur}
      />
      <Button onClick={onButtonClick}>go!</Button>
      {/* Тут 2 пункта:
        1) Нужно вместо тернарника сделать синтаксис такой: {hasError && <ErrorText>You need to fill name and text areas :c</ErrorText>}
        2) Добавить этот самый стейт hasError:
        2.1) объявить его выше через useState так же
        2.2) в событии отправки (в else блоке где TODO) делать setHasError в true
        2.3) в событии отправки (если name и text есть) делать setHasError false
        // все, это готово
      */}
      {hasError && <ErrorText>You need to fill name and text areas :c</ErrorText>}
    </Form>
  )
}

