import * as React from 'react'
import styled from 'styled-components'
import { Post } from './types'

const Form = styled.div`
  top: 0;
  position: sticky;
  margin: 0 auto;
  margin-bottom: 5%;
  padding-top: 5%;
  @media (max-width: 320px) {
    padding-top: 5px;
  }

  max-width: 400px;
  display: flex;
  flex-direction: column;
`

interface ValidProps {
  error: boolean;
}

const Input = styled.input<ValidProps>`
  padding: 10px;
  background-color: #746087;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  border-radius: 10px;
  outline: none;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Textarea = styled.textarea<ValidProps>`
  min-height: 100px;
  padding: 10px;
  border-radius: 15px;
  outline: none;
  border: 1px solid ${props => props.error ? 'red' : '#1e1d1f'};
  background-color: #746087;
  font-family: 'Tourney', cursive;
  font-weight: 800;
`

const Button = styled.button`
  margin-top: 7%;
  max-width: 120px;
  max-height: 40px;
  outline: none;
  border-radius: 30px;
  background: linear-gradient(45deg, #66538f 30%, #7984e8 60%, #2d3159);
  border: transparent;
  color: #000;
  font-size: 28px;
  font-family: 'Bungee Shade', cursive;
  font-weight: 900;
  cursor: pointer;
  letter-spacing: 1px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  transition-duration: 0.5s;
  top: 0;
  position: relative;
  &:hover {
    top: 5px;
  }
`

const Label = styled.label`
  margin-top: 25px;
  margin-bottom: 8px;
  color: #746087;
  font-size: 20px;
  font-family: 'Tourney', cursive;
`

enum Invalid {
  Both = 0,
  Name = 1,
  Text = 2,
  Nothing = 3
}

interface Props {
  onSend: (post: Post) => void
}

export function PostSendForm(props: Props) {
  const [name, setName] = React.useState('')
  const [text, setText] = React.useState('')
  const [invalid, setInvalid] = React.useState(Invalid.Nothing) 

  const onButtonClick = () => {
    const post: Post = {
      name,
      text,
    }
    
    // if(!name && !text) { 
    //   setInvalid(Invalid.Both)
    // } else {
    //   setInvalid(Invalid.Nothing)
    // }

    props.onSend(post)
    setText('')
  }

  const onKeyEnter = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      onButtonClick()
      e.preventDefault()
    }
  }
  
  const onFormBlur = () => { //шо тут? а че у меня в сервере кайнд пинг а не пинг понг() понг на клиенте)бля (
  // а как увидеть щас нет там(ну лан)) в девтулз, нетворки вкладка и там вебскет ws
  // надо перезагрузить просто че сервер? не страничку в браузере
    // мы кстати тупые х2)
    // надо при both тоже еррор тру делать
    //где внизу, я добавил, в пропсах
    
  if (!name && !text) { 
    setInvalid(Invalid.Both)
  }
  if (name && text) {
    setInvalid(Invalid.Nothing)
  }
}

  const onNameBlur = () => {
    if (!name) {
      setInvalid(Invalid.Name) 
    }
    onFormBlur()
  }

  const onTextBlur = () => {
    if (!text) {
      setInvalid(Invalid.Text)
    }  
    onFormBlur()
  }


  // А ШО ТЫ ЩА МЕНЯЛ?
  // ЕСЛИ И НЕЙМ И ТЕКСТ ЕСТЬ ТО ТОЛЛЬКО ТОГДА НАСИНГ(будто средство от насморка) 
  // ммм))) солевое) я узнала вчера что есть солевой никотин)000для вейпов там и тд эмммя чет хз ну лан
// так да, мы обсуждали же с тобой не?
  return (
    <Form>
      <Label>Your name</Label>
      <Input
        value={name}
        onChange={(e) => setName(e.target.value)} 
        error={invalid === Invalid.Name || invalid === Invalid.Both} 
        onBlur={onNameBlur}
      />
      <Label>Share what`s in your mind</Label>
      <Textarea
        value={text}
        onKeyPress={onKeyEnter}
        onChange={(e) => setText(e.target.value)} 
        error={invalid === Invalid.Text || invalid === Invalid.Both}
        onBlur={onTextBlur}
      />
      <Button onClick={onButtonClick}>go!</Button>
    </Form>
  )
}

// чистата и парядак - помогаю форматировать, я ручной приттир))0) ахахаха 
// ура, теперь у нас есть не только 4 позиции)
// теперь создай эту функцию стрелочную, рядом с onButtonClick (ниже или выше)
// вне формы? или как внутри функции  (компоненты) PostSendForm (т)
// так, у Textarea и Input есть встроенные пропсы (обработчки событий) onBlur
// надо туда передать свою функцию
// создай стрелочные onNameBlur и onTextBlur и передай туда их (можно перед onButtonClick  )
// типа onBlur={onNameBlur} - в пропсы Textarea и Input'a
// фффврввврр куда
