      // –ª–æ–ª, –∑–∞—Ç–æ –≤–µ—Å–µ–ª–æ) —è –º–± –ø–æ–∫–∞ –≥–æ–ª–æ–≤—É –ø–æ–º–æ—é? —Ö–∞—Ö–∞ –æ–∫, –ø–æ–∫–∞ –ø—Ä–æ—Å—Ç–æ setValid –∏ valid –Ω–∞–¥–æ –ø–µ—Ä–µ–º–∏–µ–æ–≤–∞—Ç—å –∏ –Ω–∞—á–∞—å–Ω–æ–µ —Å–æ—Å–æ—Ç—è–Ω–µ –ø–æ–º–µ–Ω—è—Ç—å
      // –ø–æ—Ç–æ–º if else –¥–æ–ø–∏—Å–∞—Ç—å,  —è –±—ã—Å—Ç—Ä–æ
      // –∞ —Å–∞–º–æ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ —Ü–≤–µ—Ç–æ–≤ —á—É—Ç—å –¥–∞–ª—å—à–µ —Ä–∞—Å—Å–º–æ—Ç—Ä–∏–º, —Å–Ω–∞—á–∞–ª–∞ —ç—Ç–æ —Å–¥–µ–ª–∞–µ–º –µ–±–∞–Ω—ã–π —Ä–æ—Ç –∫–∞–∑–∏–Ω–æ –±–ª—è—Ç—å - —à–æ —Ç–∞–∫–æ–µ? –ø–æ —Å—É—Ç–∏ –æ–±—ã—á–Ω—ã–π If else –Ω–∞–¥–æ
  }

  const onKeyEnter = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      onButtonClick()
      e.preventDefault()
    }
  }

  return (
    <Form>
      <Label>Your name</Label>
      <Input value={name} onChange={(e) => setName(e.target.value)} error={invalid === Invalid.Name}/>
      <Label>Share what`s in your mind</Label>
      <Textarea
        value={text}
        onKeyPress={onKeyEnter}
        onChange={(e) => setText(e.target.value)} 
        error={invalid === Invalid.Text}
      />
      <Button onClick={onButtonClick}>go!</Button>
    </Form>
  )
}

