import React from 'react'
import styled from 'styled-components'
import asteroid from './assets/asteroid.svg'
import { PostsList } from './PostsList'
import { Posts } from './Data'
import { PostSendForm } from './PostSendForm'

const Wrapper = styled.section`
  max-width: 100%;
  margin: 0 auto;
  padding: 0 20px 15px;
`
const Main = styled.section`
  height: 100vh;
`
const Title = styled.h1`
  font-size: 56px;
  line-height: 80px;
  text-align: center;
  font-family: 'Tourney', cursive;
  font-weight: 600;
  background: linear-gradient(45deg, #41265c 33%, #2efef7 66%, #5f04b4);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  @media (max-width: 320px) {
    margin-top: 5px;
    margin-bottom: 0;
  }
`

const Img = styled.img`
  width: 60px;
  height: 60px;
  max-width: 100%;
`

const postsInteractive = new Posts()

export function App() {
  const [posts, setPosts] = React.useState(postsInteractive.getPosts())
  
  /** @param {{name: string, text: string}} post */
  const onSend = (post) => {
    // когда мы нажмем на кнопку, сначала сработает onButtonClick в PostSendForm, 
    // а оттуда мы вызваем props.onSend(post) - как раз эту функцию
    // заметь, что мы передаем в onSend post, а значит тут можем его принять, это просто параметр функции
    // в данном случае any не дает абсолютно ничего. Но мы знаем, что мы передаем объект {text, name}

    // у нас есть posts (в стейте) стоп покажи еще раз наш стейт...просто вот юз стейт? это весь стейт типа?  подожди пару мин мама зашла
    // useState мы создали стейт и сохранили его значение в posts (массив)
    // А еще есть новый пост (post). Нам надо новый пост сконккетить с текущими (concat)
    // А результат объединения сделать новым стейтом (обновить posts через setPosts)
    // Я В АЛЬТАБЕ если шо, пиши в ТГ, когда вернешься :3 что ты делаешь тут? проверяю, что я правильно ли продумал все))
    // ты тут? ну типа
    // я это все удалю тогда ща
    const newPost = {
      key: Math.random().toString().slice(2),
      date: new Date().toLocaleTimeString(),
      name: post.name,
      text: post.text
    }
    setPosts([...posts, newPost])
  }
  return (
    <Wrapper>
      <Main>
        <Title>
          Cozy Space <Img src={asteroid} alt="Asteroid" />
        </Title>
        <PostSendForm onSend={onSend}/>
      </Main>
      <PostsList posts={posts} />
    </Wrapper>
  )
}
